(function () {
  /** @type {CompositeX.MetaNodeConfig} */
  const nodeConfig = {
    config: {
      name: "Base64",
      desc: "Base64 codec",
      input: { type: "string" },
      output: { type: "string" },
      options: [
        {
          name: "type",
          type: "enum",
          enumItems: [
            { name: "decode", value: "decode" },
            { name: "encode", value: "encode" },
          ],
          default: "decode",
        },
      ],
    },
    run(input, options, context) {
      if (options.type === "decode") {
        return window.atob(input)
      } else {
        return window.btoa(input)
      }
    },
  }
  return nodeConfig
})()
