(function () {
    /** @type {CompositeX.MetaNodeConfig} */
    const nodeConfig = {
      config: {
        name: "MainWorld",
        desc: "Get main world info",
        input: { type: "string", desc: "expression" },
        output: { type: "any" },
        options: [
          { name: "expression", type: "string" },
          { 
            name: "type",
            desc: "choose eval if website support it or choose exec which has limits",
            type: "enum",
            enumItems: [{ name: "eval", value: "eval"}, { name: "exec", value: "exec" }],
            default: "eval",
          }
        ],
      },
      run(input, options, context) {
        return context.mainWorld(input || options.expression, { type: options.type })
      },
    }
    return nodeConfig
  })()

(function () {
  /** @type {CompositeX.MetaNodeConfig} */
  const nodeConfig = {
    config: {
      name: "Pinyin",
      desc: "Pinyin convertion based on pinyin-api.vercel.app",
      input: { type: "string" },
      output: { type: "any" },
      options: [
        { name: "apiOptions", type: "json" },
        {
          name: "outputType",
          type: "enum",
          default: "list",
          enumItems: [
            { name: "text", value: "text" },
            { name: "list", value: "list" },
          ],
        },
      ],
    },
    async run(input, options, context) {
      const url = new URL("https://pinyin-api.vercel.app/api/generate")
      url.searchParams.append("wd", input)
      Object.entries(options.apiOptions || {}).forEach(([key, value]) => {
        url.searchParams.append(key, value)
      })
      const result = await fetch(url.toString()).then((res) => res.json())
      if (options.outputType === "text") {
        return result.join(" ")
      } else {
        return result
      }
    },
  }
  return nodeConfig
})()
